    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        String username = txtusername.getText();
        String newPassword = txtpassword.getText();
        String question = txtquestion.getText();
        String answer = txtanswer.getText();
        
       try{
            //Class.forName("com.mysql.jdbc.Driver");
            //Connection con = DriverManager.getConnection("jdbc:mysql://localhost:3306/pakanstore","root","123");
            //Statement st = con.createStatement();
            //ResultSet rs = st.executeQuery("select * from user where username ='"+username+"' and answer='"+answer+"'");
            
           String sql = "SELECT * FROM user WHERE username = '"+txtusername.getText()+"' and answer='"+answer+"'";
           java.sql.Connection conn = (Connection)koneksi.configDB();
           java.sql.PreparedStatement pst = conn.prepareStatement(sql);
           java.sql.ResultSet rs = pst.executeQuery(sql);
           
           if(rs.next()){
              pst.executeUpdate("update user set password='"+newPassword+"' where username='"+username+"' and answer='"+answer+"'");
              JOptionPane.showMessageDialog(null, "Your Password is Succefully Update");
              setVisible(false);
              new LoginAPP().setVisible(true);
           }
           else
              JOptionPane.showMessageDialog(null, "Please Write Correct Username or Answer*");
          
        }
        catch(Exception e){
             JOptionPane.showMessageDialog(null, "Erorr In Connection");
        }
    }//GEN-LAST:event_jButton2ActionPerformed

