    private void jLabel2MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jLabel2MouseClicked
      String username = txtusername.getText();
        try{
           // Class.forName("com.mysql.jdbc.Driver");
            //Connection con = DriverManager.getConnection("jdbc:mysql://localhost:3306/pakanstore","root","123");
            //Statement st = con.createStatement();
            //ResultSet rs = st.executeQuery("select question from user where username ='"+username+"'");
           String sql = "SELECT * FROM user WHERE username = '"+txtusername.getText()+"'";
           java.sql.Connection conn = (Connection)koneksi.configDB();
           java.sql.PreparedStatement pst = conn.prepareStatement(sql);
           java.sql.ResultSet rs = pst.executeQuery(sql);
           
           if(rs.next()){
               txtquestion.setText(rs.getString(5));
           }
           else
               JOptionPane.showMessageDialog(null, "Please write correct Username");
           conn.close();
           rs.close();
        }
        catch(Exception e){
             JOptionPane.showMessageDialog(null, "Error In Connection");
        }
              
    }//GEN-LAST:event_jLabel2MouseClicked

